namespace Core;
class Error
{
	# debug 开启状态
    protected $debug;
    public function __construct($debug = true)
    {
        $this->debug = $debug;
    }
    public function bootstrap()
    {
        error_reporting(0);
        # 自定义错误处理
        set_error_handler([$this, 'error'], E_ALL | E_STRICT);
    }
    public function error($errno, $error, $file, $line)
    {
        $msg = $error . "($errno)" . $file . " ($line).";
        switch ($errno) {
            case E_USER_ERROR:
            case E_USER_NOTICE:
                break;
            case E_NOTICE:
            	# 开启debug时显示提示消息
                if ($this->debug)
                    echo $msg;
                break;
            default:
                if ($this->debug) {
                    include 'debug.php';
                } else {
                    $file =  'logs/' . date('Y_m_d') . '.log';
                    # 记录错误日志到文件
                    error_log(date("[ c ]") . $msg . PHP_EOL, 3, $file, null);
                }
                die;
        }
    }
}
(new Error(false))->bootstrap();
include('houdunren.com');

