                var data = JSON.parse(response.data);
                switch (data.type) {
                    case 'init':
                        axios.get('/chat/welcome');
                        break;
                    case 'welcome':
                        //欢迎新同学
                        this.push("欢迎 <b>" + data.name + "</b> 来到直播间");
