                        //聊天消息
                        let content = '<b>' + data.name + '</b> 说：' + data.content;
                        this.push(content);
                        break;
                }
            },
            send() {
                var con = $.trim($("[name='content']").val());
                if (con) {
                    axios.post('/chat/send', {content: con});
                    $("[name='content']").val('');
                }
            },
            push(message) {
                var li = "<li>" + message + "</li>";
                $("#chats").prepend(li);
            }
        }
    }
</script>
